const clickedItem = Inputs.clickedItem;

if (!clickedItem) return;

switch (clickedItem) {
    case "profile":
    Outputs.toProfile();
    break;

    case "logIn":
    Outputs.toLogin();
    break;

    case "logOut":
    Outputs.toLogout();
    break;
}

